require( 'workflows.php' );
$w = new Workflows();

$w->result( 'hostname', "$query  hostname", "$query", 'Set your base hostname to '.$query, 'icon.png', 'yes', "$query" );
$w->result( 'username', "$query  username", "$query", 'Set your username to '.$query, 'icon.png', 'yes', "$query" );
$w->result( 'password', "$query  password", "$query", 'Set your password to '.$query, 'icon.png', 'yes', "$query" );

echo $w->toxml();

require('workflows.php');
$wf = new Workflows();

$orig = "{query}";

$hostname = $wf->get( 'hostname', 'settings.plist' );
$user = $wf->get( 'username', 'settings.plist' );
$password = $wf->get( 'password', 'settings.plist' );
$basicInfo = $user.":".$password;
$filter = $wf->get( 'filter', 'settings.plist' );
$filter = str_replace('::left::', '(', $filter);
$filter = str_replace('::right::', ')', $filter);
$filter = str_replace('https://'.$hostname, '', $filter);

$url = "https://".$basicInfo."@".$hostname;

$xml = $wf->request($url.$filter);
$xml = json_decode($xml,true);
$int = 1;

foreach ($xml['issues'] as $item)
{
	$wf->result( 'issue', 'https://'.$hostname.'/browse/'.$item['key'], $item['key']." : ".$item['fields']['summary'], "Type: ".$item['fields']['issuetype']['name'].'  Status: '.$item['fields']['status']['name']."   Assigned to: ".$item['fields']['assignee']['displayName']."  Priority: ".$item['fields']['priority']['name'], 'icon.png');
};

$results = $wf->results();
if ( count( $results ) == 0 ):
	$wf->result( 'jira', 'No Suggestions', 'No Suggestions', 'No Issues found.', 'icon.png' );
endif;

echo $wf->toxml();

