$query = "{query}";

require( 'workflows.php' );
$w = new Workflows();

$tokens = explode("||", $query);

$name = "$tokens[0]";
$filter = "$tokens[1]";

$filter = str_replace('?', '\?', $filter);
$filter = str_replace('+', '\+', $filter);
$filter = str_replace('(', '::left::', $filter);
$filter = str_replace(')', '::right::', $filter);


$w->set( 'filterName', $name, 'settings.plist' );
$w->set( 'filter', $filter, 'settings.plist' );

echo 'Set Filter to '.$name;

require('workflows.php');
$wf = new Workflows();

$orig = "{query}";

$tokens = explode("||", $orig);

$name = "$tokens[0]";
$id = "$tokens[1]";

$hostname = $wf->get( 'hostname', 'settings.plist' );
$user = $wf->get( 'username', 'settings.plist' );
$password = $wf->get( 'password', 'settings.plist' );
$basicInfo = $user.":".$password;

$url = "https://".$basicInfo."@".$hostname;

$data = array("fields" => array("assignee" => array("name" => $name)));                                                                    
$data_string = json_encode($data);

$xml = $wf->requestDataPUT($url."/rest/api/2/issue/".$id, $data_string, "PUT");

if($xml == ''):
	echo "Success! Your job has been posted!";
else:
	echo "Error:".$xml;
endif;

